"use strict";

function onScroll() {
    var o = $(document).scrollTop();
    $(menu_selector + " a").each(function () {
        var e = $(this).attr("href"),
            t = $(e);
        t.position().top <= o && t.position().top + t.outerHeight() > o ? (links.removeClass("sidebar__link--active"), $(this).addClass("sidebar__link--active")) : $(this).removeClass("sidebar__link--active")
    })
}

function initMap() {
    var o = {
            lat: 1.312717,
            lng: 103.900014
        },
        e = new google.maps.Map(document.getElementById("map"), {
            zoom: 16,
            center: o
        });
    new google.maps.Marker({
        position: o,
        map: e
    })
}
$(document).ready(function () {
    $("#top-menu").on("click", "a", function (o) {
        o.preventDefault();
        var e = $(this).attr("href"),
            t = $(e).offset().top;
        $("body,html").animate({
            scrollTop: t
        }, 700)
    })
});
var menu_selector = "#menu",
    links = $(".sidebar__link");
$(document).ready(function () {
    $(document).on("scroll", onScroll), $("#menu").on("click", "a", function (o) {
        o.preventDefault(), $(document).off("scroll"), links.removeClass("sidebar__link--active"), $(this).addClass("sidebar__link--active");
        var e = $(this).attr("href"),
            t = $(e);
        $("html, body").animate({
            scrollTop: t.offset().top
        }, 500, function () {
            window.location.hash = e, $(document).on("scroll", onScroll)
        })
    })
});